import React from 'react';
import {connect} from 'react-redux';

class userEdit extends React.Component{

    constructor(props){
        super(props)
        const { userDetails } = this.props
        this.state = {
            userInfo :userDetails
        }
        
    }
    _infoSetState(e){
        console.log(this.state,' inforSetState 변경 사항')
        this.setState=({
            
        })
    }

    onChange(event){
        const target = event.target;
        const value = event.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
        console.log(this.state)
    }

    render(){
        const { userDetails } = this.props;
        console.log('현재의 state >>>>', this.state)
        console.log("현재의 props", this.props);
        console.log("render userDetails->>>>>",userDetails);
        console.log("position?--->", userDetails.position);
        return(
            <div className="edituser">
                <h3>회원정보 수정</h3>
                <div class="edit-table">
                    <table summary="">
                        <tbody>
                            <tr>
                                <td scope="row">회원 ID</td>
                                <td>{userDetails.userId}</td>
                            </tr>
                            <tr>
                                <td scope="row">비밀번호</td>
                                <td><input type="password" name="password" value={userDetails.userPassword} onChange={this.onChange} placeholder="변경할 비밀번호 입력"/></td>
                            </tr>
                            <tr>
                                <td scope="row">고객 이름</td>
                                <td>{userDetails.username}</td>
                            </tr>
                            <tr>
                                <td scope="row">Email</td>
                                <td>{userDetails.userEmail}</td>
                            </tr>
                            <tr>
                                <td scope="row">고객성별</td>
                                <td>{userDetails.position.customer_gender}</td>
                            </tr>
                            <tr>
                                <td scop="row">핸드폰 번호</td>
                                <td><input type="phone" name="phone" value={userDetails.position.customerPhone} onChange={this.onChange} placeholder="'-'없이 입력"/></td>
                            </tr>
                            <tr>
                                <td scope="row">주소</td>
                                <td><input type="address" name="address" value={userDetails.position.customerAddr} onChange={this.onChange} placeholder="주소입력"/></td>
                            </tr>
                            <tr>
                                <td scope="row">생년월일</td>
                                <td>{userDetails.position.customerbirth}</td>
                            </tr>
                            <tr>
                                <td scope="row">고객 등급</td>
                                <td>{userDetails.position.customerGrade}</td>
                            </tr>
                            <tr>
                                <td scope="row">최근 접속일자</td>
                                <td>{userDetails.userLastConnection}</td>
                            </tr>
                            <tr>
                                <td scope="row">가입일자</td>
                                <td>{userDetails.userRegday}</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }

}

function mapStateToProps(state) {
    const { auth } = state;
    const { userDetails } = auth;
    console.log(userDetails,' <-----userdetails ')
    console.log(userDetails.position, 'customer?')
    return {
        userDetails
    };
}//mapStateToProps

export default connect(mapStateToProps)(userEdit);
