  constructor(props){
    super(props)
  }

  componentWillMount(){
    const { loadNoticeBoard } = this.props;
    loadNoticeBoard();
  }

  render(){
  const { noticeBoard } = this.props;
  const { items } =noticeBoard;
  console.log('00000000000000000000000000000000000000',noticeBoard)
  return (
      <div className="main">
        <div className="container">
        
          {/* BEGIN SIDEBAR & CONTENT */}
        
