import React from 'react';
import {connect} from 'react-redux';

class userEdit extends React.Component{

    constructor(props){
        super(props)
        const { userDetails } = this.props
        this.state = {
            userInfo :userDetails
        }
        
    }
    _infoSetState(e){
        console.log(this.state,' inforSetState 변경 사항')
        this.setState=({
            
        })
    }

    onChange(event){
        const target = event.target;
        const value = event.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
        console.log(this.state)
    }

    render(){
        const { userDetails } = this.props;
        console.log('현재의 state >>>>', this.state)
        console.log("현재의 props", this.props);
        console.log("render userDetails->>>>>",userDetails)
        return(
            <div className="edituser">
                <h3>회원정보 수정</h3>
                <div class="edit-table">
                    <table summary="">
                        <tbody>
                            <tr>
                                <td scope="row">회원 ID</td>
                                <td>{userDetails.userId}</td>
                            </tr>
                            <tr>
                                <td scope="row">비밀번호</td>
                                <td><input type="text" name="password" value={this.state.userDetails.userPassword} onChange={this.onChange} placeholder="변경할 비밀번호 입력"/>{this.state.userDetails.userPassword}</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }

}

function mapStateToProps(state) {
    const { auth } = state;
    const { userDetails } = auth;
    console.log(userDetails,' <-----userdetails ')
    return {
        userDetails
    };
}//mapStateToProps

export default connect(mapStateToProps)(userEdit);
