import React from 'react';
import ProductList from './ProductList';
import { connect } from 'net';
import { Link } from 'react-router-dom';
import { Actions } from '../actions';

class product extends React.Component{

    constructor(props){
        super(props)
        this.state={
            type:'lower',
            items:[]
        }
    }

    componentDidMount(state){
        const {loadProductList} = this.state;
        loadProductList(this.state.type, 2)
    }

    _AllProducts = () => {
        const {items} = this.props;
        var productItems=[];

        if(items!==undefined && items!==null){
            productItems=items.map(item=>{
                return <Link to={`/productdetails/${item.productBoardNum}`}>
                    <ProductList key={item.productBoardNum} item={item}/>
                </Link>
            })
        }

        return productItems
    }

    reder(){
        return(
            <div>
                {this._AllProducts()}
            </div>
        );
    }
}

const mapDispatchToProps = dispatch => ({
    loadProductList:(type,id) => dispatch(Actions.loadProductList(type,id))
});

function mapStateToProps(state){
    const {product} = state;
    const {productBoard} = product;
    return{
        productBoard
    };
}

export default connect(mapStateToProps, mapDispatchToProps)(product);
