function mapStateToProps(state) {
    const { auth } = state;
    console.log(auth, "auth....");
    const { userDetails } = auth;
    console.log(userDetails, "userdetail....");
    return {
        userDetails // 배열...
    };
}
export default connect(mapStateToProps)(MyPage);
