import React from 'react';
import { Actions } from '../../actions/index';
import { ActionTypes } from '../../contants';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';



class QnABoardWrite extends React.Component{

    constructor(props){
        super(props)
        const {userDetails } = this.props;
        this.state={
            userId:userDetails.userId,
            questionBoardPassword :'',
            questionBoardTitle:'',
            questionBoardContent:'',
        }
        console.log(props)
    }

    componentWillMount(){

    }
    _onClickAction(){
        window.close();
    }

    render(){
        return(
           
		<div class="container" role="main">
			<h2>board Form</h2>
			<form name="form" id="form" role="form" method="post" action="${pageContext.request.contextPath}/board/saveBoard">
				<div class="mb-3">
					<label for="title">제목</label>
					<input type="text" class="form-control" name="title" id="title" placeholder="제목을 입력해 주세요"/>
				</div>			
				<div class="mb-3">
					<label for="reg_id">작성자</label>
                    <input type= "text">{this.state}</input>
				</div>	
				<div class="mb-3">
					<label for="content">내용</label>
					<textarea class="form-control" rows="5" name="content" id="content" placeholder="내용을 입력해 주세요" ></textarea>
				</div>	
			</form>
			<div >
				<button type="button" class="btn btn-sm btn-primary" onClick={this._onClickAction}>저장</button>
				<button type="button" class="btn btn-sm btn-primary" id="btnList">목록</button>
			</div>
		    </div>
        )
    }

}
const mapStateToProps =(state) =>{

    const{ userDetails } = state.auth;
    const{ qnaBoard } = state.product;
    const{ data }   = qnaBoard;


    console.log(qnaBoard,' qnaAnswerWrite Board  <--- MapStateToProps')
    console.log(data,' <-------- data')
    console.log(data.boardNum,' <------- boardNum')
    console.log(userDetails,' <----------- userDetails')
    console.log(userDetails.userId,' <----------- userId')
    return{
        data,
        userDetails
    };
}
const mapDispatchToProps = (dispatch) => ({

})

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(QnABoardWrite));
