import React from 'react';
import { Actions } from '../../actions/index';
import { ActionTypes } from '../../contants';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';



class QnABoardWrite extends React.Component {

    constructor(props) {
        super(props)
        const { userDetails } = this.props;
        const {data}=this.props
        this.state = {
            userId: userDetails.userId,
            productBoardNum:data.boardNum,
            questionBoardPassword: this.questionBoardPassword,
            questionBoardTitle: this.questionBoardTitle,
            questionBoardContent:''
            
        }
        // this._onChangeInput = this._onChangeInput.bind(this);
    }

    componentWillMount() {
    }
    shouldComponentUpdate(){
        return(this.state)
    }
    _onClickAction() {
        // const writeForm ={
        //     userId = this.state.userId
        //     questionBoardPassword = this.state.questionBoardPassword;
        // }
        window.close('');
    }
    _onChangeInput(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        console.log(target.name, ' name')
        console.log(value)
        this.setState({
            [name]:value
        });
        console.log(this.state)
    }

    render() {
        return (

            <div className="container" role="main">
                <h2>상품 Q&A 게시판</h2>
                <form onSubmit={this._onClickAction}>
                    <div className="mb-3">
                        <label >제목</label>
                        <input type="text" className="form-control" name="title" id="title" placeholder="제목을 입력해 주세요" />
                    </div>
                    <div className="mb-3">
                        <label >작성자</label>
                        <div>
                            <b>{this.state.userId} </b>
                        </div>
                    </div>
                    <div>
                        <label>비밀번호</label>
                        <div>
                            <input type="password" maxLength="4" placeholder="4자리"></input>
                        </div>
                    </div>
                    <div className="mb-3">
                        <label >내용</label>
                        <textarea className="form-control" rows="5" name="questionBoardContent" id="content" value={this.state.questionBoardContent.value}
                            onChange={this._onChangeInput.bind(this)} placeholder="내용을 입력해 주세요" ></textarea>
                    </div>
                </form>
                <div >
                    <button type="submit" className="btn btn-sm btn-primary">저장</button>
                    <button type="reset" className="btn btn-sm btn-primary" >목록</button>
                </div>
            </div>
        )
    }

}
const mapStateToProps = (state) => {

    const { userDetails } = state.auth;
    const { qnaBoard } = state.product;
    const { data } = qnaBoard;


    console.log(qnaBoard, ' qnaAnswerWrite Board  <--- MapStateToProps')
    console.log(data, ' <-------- data')
    console.log(data.boardNum, ' <------- boardNum')
    console.log(userDetails, ' <----------- userDetails')
    console.log(userDetails.userId, ' <----------- userId')
    return {
        data,
        userDetails
    };
}
const mapDispatchToProps = (dispatch) => ({

})

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(QnABoardWrite));
